function App() {


    const productos = [{Nombre:"Subscripci√≥n Premium",Descripcion:"Paquete premium ideal para grandes empresas.",Stock:3, boton:"Suscribirse"},
{Nombre:"Subscripci√≥n Platino",Descripcion:"Paquete Platino ideal para grandes/medianas empresas.",Stock:5, boton:"Suscribirse"},
{Nombre:"Subscripci√≥n Bronce",Descripcion:"Paquete Bronce ideal para quienes reci√©n estan comenzando.",Stock:2, boton:"Suscribirse"}]

const [prods,setProds] = useState([])
useEffect(() => {

