import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import ItemDetail from "./ItemDetail";

const ItemDetailContainer = () => {
  //detalle de mi prod
 const [idFilt, setIdFilt] = useState(null);

  const data = [
    {
      id: 1,
      Nombre: "Suscripcion Premium",
      Precio: "U$D" + " " + 325,
      img: "https://i.ibb.co/b6fjcC5/PREMIUM.png",
    },
    {
      id: 2,
      Nombre: "Suscripcion Platino",
      Precio: "U$D" + " " + 262,
      img: "https://i.ibb.co/frDf6s8/PLATA.png",
    },
    {
      id: 3,
      Nombre: "Suscripcion Bronce",
      Precio: "U$D" + " " + 140,
      img: "https://i.ibb.co/qrn41dK/BRONZ.png",
    },
  ];
  const { id } = useParams();
  console.log(id);
  const ProdFind = data.find((prod) => prod.id == id);
  console.log(ProdFind);

  useEffect(() => {
    const ReturnItem = new Promise((resolve, reject) => {
    setTimeout(() => {
      
      resolve(ProdFind);
    }, 2000);
    
    });
    ReturnItem.then((res) => {
      setIdFilt(res);
    });
  }, []);

 
  return (
    <div>
    {idFilt ? <ItemDetail idFilt={idFilt}/>:<h1>Carregando</h1>}
    </div>
  );
};

export default ItemDetailContainer;
