import React, { useRef } from 'react';
import emailjs from 'emailjs-com';
import "./Contact.css"


const ContactUs = () => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_gnaygob', 'template_prv11tb', form.current, 'LzGo5aMLcr8ZSb-ek')
      .then((result) => {
          alert("enviado")
          console.log(result)
      }, (error) => {
          alert("error")
          console.log(error.text);
      });


  };

  return (
      <div className='ContactCont '>
    
    <form className='d-flex flex-column p-4' ref={form} onSubmit={sendEmail}>
        <h3 className='text-center text-light mb-3'>Contacto</h3>
      <label>Nombre</label>
      <input type="text" name="user_name" />
      <label>Apellido</label>
      <input type="text"/>
      <label>Email</label>
      <input type="email" id="email" name="from_name"/>
      <label>Mensaje</label>
      <textarea name="message" />
      <button className='mt-3 btn' type="submit" value="Send">Enviar </button>
    </form>
    </div>
  );
  
};

export default ContactUs;
