import React, { useState } from "react";
import ItemList from "./ItemList";



const Item = ({prods}) => {
 const [stock,setStock] = useState(0)

 let stockAct = prods.Stock - stock 
 let myProds = prods.Stock
 let Total = stock
console.log(stockAct)
  return (
    <div className="card cardStyle bg-dark d-flex" style={{width:"18rem"}}>
       <div className="card-body text-white">
     <h1 className="card-title" key={prods.id} >{prods.Nombre}</h1>
     <p className="card-text "> {prods.Descripcion}</p>
     <p>Disponibles {stockAct}</p>
      <button className="m-2 badge bg-danger" onClick={()=>{stock < myProds ? setStock(stock + 1): alert("Sin Stock Disponible")}} > + </button>
      <p>En el carro: {Total}</p>
      <button className="m-2 badge bg-danger" onClick={()=>{stock > 0 ? setStock(stock - 1):alert("agrega un prodcuto al menos")}}> - </button>
     </div>
    </div>
  );
}; 

export default Item;
